mapper.select:
    SELECT u.userName,u.realName,u.mobile,u.createDate,s.gender,s.birthday,s.userId
    FROM
	t_user u
    INNER JOIN t_user_record s ON u.id = s.userId
    WHERE u.id = s.userId

      AND u.userName = #{userName}


      AND u.realName = #{realName}


      AND u.mobile = #{mobile}

      limit #{curPage},#{pageSize}

    SELECT count(1) FROM(
      SELECT u.userName,u.realName,u.mobile,u.createDate,s.gender,s.birthday,s.userId
      FROM
	t_user u
    INNER JOIN t_user_record s on u.id = s.userId
    WHERE u.id = s.userId

      AND u.userName = #{userName}


      AND u.realName = #{realName}


      AND u.mobile = #{mobile}

)m

SELECT u.realName,u.mobile,r.gender,r.birthday,r.weight,r.userId FROM
t_user u
INNER JOIN t_user_record r on u.id = r.userId

INNER JOIN t_user_vip v on v.userId = u.id


INNER JOIN t_user_hobby h on v.userId = h.userId

WHERE u.`status` = '0'

 AND v.`status` = '0'


  AND (u.county LIKE CONCAT("%", #{province} ,"%"))


   AND r.gender = #{gender}


  AND #{endAge} >=  r.birthday


  AND  r.birthday >= #{startAge} 


  AND r.weight = #{weight}


  AND h.hobbyId = #{label}


  AND u.mobile = #{mobile}

    limit #{curPage},#{pageSize}

SELECT u.id as userId,app.deviceToken,app.OS FROM t_user u INNER JOIN t_user_record r on u.id = r.userId INNER JOIN t_user_app_push app on app.userId = u.id AND app.display = TRUE
INNER JOIN t_user_vip v on v.userId = u.id
INNER JOIN t_user_hobby h on v.userId = h.userId
WHERE u.`status` = '0'
AND v.`status` = '0'
AND (u.county LIKE CONCAT("%", #{province} ,"%"))
AND r.gender = #{gender}
AND #{endAge} * 365 >= DATEDIFF(NOW(), r.birthday)
AND DATEDIFF(NOW(), r.birthday) > #{startAge} * 365
AND r.weight = #{weight}
AND h.hobbyId = #{label}
AND u.mobile = #{mobile}
group by app.deviceToken

  SELECT COUNT(1) FROM(
  SELECT u.realName,u.mobile,r.gender,r.birthday,r.weight,r.userId FROM
  t_user u
  INNER JOIN t_user_record r on u.id = r.userId

  INNER JOIN t_user_vip v on v.userId = u.id


  INNER JOIN t_user_hobby h on v.userId = h.userId

  WHERE u.`status` = '0'

 AND v.`status` = '0'


  AND (u.county LIKE CONCAT("%", #{province} ,"%"))


  AND r.gender = #{gender}


  AND #{endAge} >=  r.birthday


  AND  r.birthday >= #{startAge} 


  AND r.weight = #{weight}


  AND h.hobbyId = #{label}


  AND u.mobile = #{mobile}

)m

SELECT count(*) FROM (SELECT count(1) memberCount,sum(CASE WHEN ur.gender = '1' THEN 1 END) man, sum(CASE WHEN ur.gender = '0' THEN 1 END) woman, ifNull(u.province, '地址数据缺失') province, CASE WHEN ISNULL(ur.birthday) THEN '生日数据缺失' WHEN DATE_FORMAT(curdate(), '%Y') - DATE_FORMAT(ur.birthday, '%Y') BETWEEN 0 AND 20 THEN '0~20岁' WHEN DATE_FORMAT(curdate(), '%Y') - DATE_FORMAT(ur.birthday, '%Y') BETWEEN 20 AND 30 THEN '20~30岁' WHEN DATE_FORMAT(curdate(), '%Y') - DATE_FORMAT(ur.birthday, '%Y') BETWEEN 30 AND 40 THEN '30~40岁' WHEN DATE_FORMAT(curdate(), '%Y') - DATE_FORMAT(ur.birthday, '%Y') BETWEEN 40 AND 50 THEN '40~50岁' WHEN DATE_FORMAT(curdate(), '%Y') - DATE_FORMAT(ur.birthday, '%Y') BETWEEN 50 AND 60 THEN '50~60岁' WHEN DATE_FORMAT(curdate(), '%Y') - DATE_FORMAT(ur.birthday, '%Y') >= 60 THEN '60岁以上' END ageAvg FROM t_user u INNER JOIN t_user_record ur ON u.id = ur.userId
LEFT JOIN t_order o ON o.userId = u.id AND o.`status` in (0,1,2) LEFT JOIN t_org_user ou ON o.orgUserId = ou.id LEFT JOIN t_org org ON org.id = ou.orgId
WHERE u.id = ur.userId
AND u.parentId = #{userNo}
AND org.orgName = #{orgName}
AND u.province = #{province}
AND ur.birthday = #{avgAge}
group by u.province) v

SELECT count(1) memberCount, sum(case when ur.gender='1' then 1 end) man, sum(case when ur.gender='0' then 1 end) woman, ifNull(u.province, '地址数据缺失') province, case WHEN ISNULL(ur.birthday) then '生日数据缺失' WHEN DATE_FORMAT(curdate(), '%Y')-DATE_FORMAT(ur.birthday, '%Y') between 0 and 20 then '0~20岁' WHEN DATE_FORMAT(curdate(), '%Y')-DATE_FORMAT(ur.birthday, '%Y') between 20 and 30 then '20~30岁' WHEN DATE_FORMAT(curdate(), '%Y')-DATE_FORMAT(ur.birthday, '%Y') between 30 and 40 then '30~40岁' WHEN DATE_FORMAT(curdate(), '%Y')-DATE_FORMAT(ur.birthday, '%Y') between 40 and 50 then '40~50岁' WHEN DATE_FORMAT(curdate(), '%Y')-DATE_FORMAT(ur.birthday, '%Y') between 50 and 60 then '50~60岁' WHEN DATE_FORMAT(curdate(), '%Y')-DATE_FORMAT(ur.birthday, '%Y') >=60 then '60岁以上' END ageAvg from t_user u INNER JOIN t_user_record ur ON u.id = ur.userId
LEFT JOIN t_order o ON o.userId = u.id AND o.status in (0,1,2) LEFT JOIN t_org_user ou ON o.orgUserId = ou.id LEFT JOIN t_org org ON org.id = ou.orgId
WHERE u.id = ur.userId
AND u.parentId = #{userNo}
AND org.orgName = #{orgName}
AND u.province = #{province}
AND ur.birthday = #{avgAge}
group by u.province
ORDER BY province ASC
ORDER BY ageAvg ASC
LIMIT #{curPage},#{pageSize}
